/**
 * Enumeration for image sizes
 * @enum {number}
 * @readonly
 * @description Image sizes in pixels for the long side of the image.
 */
export enum ImageSize {

    /**
     * 320 pixels image size
     */
    SIZE_320 = 320,

    /**
     * 640 pixels image size
     */
    SIZE_640 = 640,

    /**
     * 1024 pixels image size
     */
    SIZE_1024 = 1024,

    /**
     * 2048 pixels image size
     */
    SIZE_2048 = 2048,
}
